import { useState } from 'react';
import SliderButton from '../SliderButton';
import './index.css';

function Slider({ slides, pictures }) {
	const [slider, setSliderAnnimation] = useState({
		PicsIndex: 0,
		inProgress: false,
	});

	const nextSlide = () => {
		let PicsIndex = slider.PicsIndex;
		if (slider.PicsIndex < pictures.length - 1) PicsIndex++;
		else PicsIndex = 0;
		setSliderAnnimation({
			PicsIndex: PicsIndex,
			inProgress: true,
		});
		setTimeout(
			() =>
				setSliderAnnimation({
					PicsIndex: PicsIndex,
					inProgress: false,
				}),
			100
		);
	};

	const previousSlide = () => {
		let PicsIndex = slider.PicsIndex;
		if (slider.PicsIndex > 0) PicsIndex--;
		else PicsIndex = pictures.length - 1;
		setSliderAnnimation({
			PicsIndex: PicsIndex,
			inProgress: true,
		});
		setTimeout(
			() =>
				setSliderAnnimation({
					PicsIndex: PicsIndex,
					inProgress: false,
				}),
			100
		);
	};

	const [current, setCurrent] = useState(0); //je définis l'index du premier slide à 0
	const length = slides.length; // longueur du tableau de slides

	const nextSlideS = () => {
		setCurrent(current === length - 1 ? 0 : current + 1); // on repart au premier slide quand on arrive au dernier
	};
	const prevSlide = () => {
		setCurrent(current === 0 ? length - 1 : current - 1); // on repart au dernier slide quand on est au premier
	};

	return (
		<section id="carrousel-container">
			{length > 1 && <SliderButton direction="next" goTo={nextSlide} />}
			{length > 1 && <SliderButton direction="previous" goTo={previousSlide} />}
			{slides.map((slide, index) => (
				<div
					key={index} // mise en place du slider avec affichage conditionnel et opacity=1 quand le slide en cours vaut l'index
					className={
						current === index
							? "slider bl-msk wh-msk active-anim"
							: "slider bl-msk wh-msk"
					}
				>
					{index === current && <img src={slide} alt="appartement à louer" />}
					{index === current && (
						<span className="slider__number">
							{current + 1}/{length}
						</span>
					)}
				</div>
			))}
		</section>
	);
}
export default Slider;
